create table t_rule(rule_id int not null auto_increment,rule_name varchar(60) default null,key_word varchar(60) default null,is_word_reply int default 0,word_content varchar(60) default null,is_picture_reply int default 0,picture_content varchar(60) default null,is_voice_reply int default 0,voice_content varchar(60) default null,is_video_reply int default 0,video_content varchar(60) default null,is_picture_word_reply int default 0,picture_content1 varchar(60) default null,word_content1 varchar(60) default null,PRIMARY KEY (rule_id));